import React,{useState} from 'react'
import Styled from 'styled-components'
import {AiOutlineUser} from 'react-icons/ai'
import { IoMdAdd } from 'react-icons/io'
function Form(props) {
    const [isStepActive, setIsStepActive ]= useState([false,false,false,false]);

  return (
    
       <div>
          <div>
                <hr/>
                <StepContainer>
                    <StepBadges>
                        <span status={isStepActive[0]}></span>
                        <p>Personal Info</p>
                    </StepBadges>
                    <StepBadges>
                        <span status={isStepActive[1]}></span>
                        <p>Documents</p>
                    </StepBadges>
                    <StepBadges>
                        <span status={isStepActive[2]}></span>
                        <p>License</p>
                    </StepBadges>
                    <StepBadges>
                        <span status={isStepActive[3]}></span>
                        <p>Hourly Rate</p>
                    </StepBadges>
                    
                </StepContainer>
                <AvatorSection>
                    <AvatorContainer>
                        <AvatorIcon/>
                        <p><AvatorBadge/></p>
                    </AvatorContainer>
                </AvatorSection>
                  
                   
                   
                
                
          

            
          </div>

          <button  onClick={props.onClose}>
                Next
          </button>
       </div>    

  
  )
}

export default Form
const StepContainer= Styled.div`
display:flex;
justify-content:space-between;
align-items:center;
margin-bottom:3rem;
height: 60px;

span{ 
    width:6px;
    height: 6px;
    border-radius: 50%;
    transform:${props=>props.status? 'translateX(0)':'translateX(100%)'};
    font-size:12px;
    font-weight: bold;
    transform:translateY(-20px);
    padding:6px;

   
  

}
`
const StepBadges=Styled.div`
display:flex;
justify-content:space-around;
align-items:flex-start;
`
const AvatorSection = Styled.div`
display:flex;
justify-content:space-between;
align-items:center;



`

const AvatorContainer = Styled.div`
width:60px;
height:60px;
background:#ccc;
border-radius:50%;
transform:translateX(26vw);
cursor: pointer;

p{
    width:26px;
    height:26px;
    background:darkred;
    border-radius:50%;
 
    transform:translate(38px,-20px);
    border: 2px solid #ccdcdc;

}

`
const AvatorIcon = Styled(AiOutlineUser)`
color:white;
transform:translate(15px,15px);
font-size:30px;

`
const AvatorBadge = Styled(IoMdAdd)`
font-weight:600;
font-size:20px;
color:white !important;
font-size:20px;

`
