import React from 'react'
import Styled from 'styled-components'
import {AiOutlineUser} from 'react-icons/ai'
import { IoMdAddCircle } from 'react-icons/io'
function Form(props) {
  return (
    
       <div>
          <div>
                <form>
                <FormGroupArea>
                    <AvatorContainer>
                        <AvatorIcon/>
                        <AvatorBadge/>
                    </AvatorContainer>
                </FormGroupArea>
                    <FormGroupArea>
                    <FormControl>
                        <label htmlFor="fullname">Full Name</label>
                        <input type="text" className="form-control" id="fullname" placeholder="Full Name"/>
                    </FormControl>
                    <FormControl>
                        <label htmlFor="email">Email</label>
                        <input type="text" className="form-control" id="email" placeholder="Full Name"/>
                    </FormControl>
                    <FormControl>
                        <label htmlFor="phno">Phone Number</label>
                        <input type="text" className="form-control" id="phno" placeholder="Full Name"/>
                    </FormControl>
                    <FormControl>
                        <label htmlFor="fullname">Full Name</label>
                        <input type="text" className="form-control" placeholder="Full Name"/>
                    </FormControl>
                   
                    </FormGroupArea>
                   
                    <FormGroupArea>
                    <FormControl>
                        <label htmlFor="fullname">Full Name</label>
                        <input type="text" className="form-control" placeholder="Full Name"/>
                    </FormControl>
                    <FormControl>
                        <label htmlFor="fullname">Full Name</label>
                        <input type="text" className="form-control" placeholder="Full Name"/>
                    </FormControl>
                    <FormControl>
                        <label htmlFor="fullname">Full Name</label>
                        <input type="text" className="form-control" placeholder="Full Name"/>
                    </FormControl>
                    <FormControl>
                        <label htmlFor="fullname">Full Name</label>
                        <input type="text" className="form-control" placeholder="Full Name"/>
                    </FormControl>
                   
                    </FormGroupArea>
                </form>
                
          

            
          </div>

          <button  onClick={props.onClose}>
                Next
              </button>
       </div>    

  
  )
}

export default Form

const FormGroupArea = Styled.div`
display:flex;
justify-content:space-between;
align-items:center;


`
const FormControl = Styled.div`
display:flex;
justify-content:space-between;
flex-direction:column;
`
const AvatorContainer = Styled.div`
width:60px;
height:60px;
background:#ccc;
border-radius:50%;
transform:translateX(26vw);


AvatorBadge{
    width:26px;
    height:26px;
    background:red;
    border-radius:50%;
    color:white;
    padding-left:8px;
   transform:translate(35px,-10px);
   font-weight:600;
   font-size:20px;
  
    
}

`
const AvatorIcon = Styled(AiOutlineUser)`
color:white;
transform:translate(25px,20px);
font-size:15px;

`
const AvatorBadge = Styled(IoMdAddCircle)`
font-weight:600;
font-size:20px;

`
