import React from 'react'


import Styled from 'styled-components'

import {AiOutlineLogout} from 'react-icons/ai'
import {AiOutlineHome} from 'react-icons/ai'
import {AiOutlineUser} from 'react-icons/ai'
import {GiGuards} from 'react-icons/gi'
import {AiOutlineTeam} from 'react-icons/ai'
import {AiOutlineSetting} from 'react-icons/ai'
import {AiOutlineNotification} from 'react-icons/ai'
const Sidebar=()=> { 
  return (
    <SidebarContainer>
        <Menu>
            <a  className="sidebarlist">
                <DashboardIcon className="menuIcon"/>
                <span>Dashboard</span>
            </a>
            <a >
                <ClientIcon className="menuIcon"/>
                <span>Clients</span>
            </a>
            <a >
                <BodyGuardIcon className="menuIcon"/>
                <span>Bodyguard</span>
            </a>
            <a >
                <TeamIcon className="menuIcon"/>
                <span>Team</span>
            </a>
            <a >
                <SettingsIcon className="menuIcon"/>
                <span>Settings</span>
            </a>
            <a >
                <NotificationIcon className="menuIcon"/>
                <span>Notification</span>
            </a>
            
        </Menu>
        <Logout>
          <LogoutIcon/>
          <span>Logout</span>
        </Logout>

    </SidebarContainer>
  )
}

export default Sidebar;

const SidebarContainer = Styled.div`
    display:flex;
    flex-direction: column;
    justify-content: space-between;
    position:sticky;
    background:white;
    width:200px;
    top: 50px;
    height: calc(100vh - 50px);
    left:0;
    bottom:0;
    
    box-shadow: 0 0 10px rgba(0,0,0,.2);

    .menuIcon{

      
      font-weight:bold;
      font-size:20px;
      margin-right: 6px;

    }



`
const Menu = Styled.div`
display:flex;
justify-content:space-between;
align-items:start;
flex-direction:column;
margin-bottom: 2rem;
padding: 5px;
margin: 10px;
z-index:99;

a{
  padding:10px;
  display:flex;
  align-items:center;
  justify-content:center;
}
.sidebarlist.active,
.sidebarlist:hover {
  background-color: rgb(240, 240, 255);
}

`
const Logout = Styled.div`

bottom:0px !important;
font-weight:400;
margin-bottom:2px;
position:absolute;
margin:10px;
display:flex;
align-items: center;
justify-content:space-between;




`
//defining icons
const LogoutIcon = Styled(AiOutlineLogout)`
`

const DashboardIcon= Styled(AiOutlineHome)`
`

const ClientIcon = Styled(AiOutlineUser)`
`

const BodyGuardIcon= Styled(GiGuards)`
`
const TeamIcon = Styled(AiOutlineTeam)`
`

const SettingsIcon= Styled(AiOutlineSetting)`
`
const NotificationIcon = Styled(AiOutlineNotification)`
`
