import React from 'react'
import {Link} from 'react-router-dom'
import Styled from 'styled-components'
import {AiOutlineLogout} from 'react-icons/ai'
import {AiOutlineHome} from 'react-icons/ai'
import {AiOutlineUser} from 'react-icons/ai'
import {GiGuards} from 'react-icons/gi'
import {AiOutlineTeam} from 'react-icons/ai'
import {AiOutlineSetting} from 'react-icons/ai'
import {AiOutlineNotification} from 'react-icons/ai'
import {AiOutlineClose} from 'react-icons/ai'
const Sidebar=(props)=> { 
  return (
    <SidebarContainer show={props.show}>

      
        <Menu>
        <CloseContainer>
      <CloseBtn/>
      </CloseContainer>
            <Link to="/dashboard"  className="sidebarlist">
                <DashboardIcon className="menuIcon"/>
                <span>Dashboard</span>
            </Link>
            <Link to="client" >
                <ClientIcon className="menuIcon"/>
                <span>Clients</span>
            </Link>
            <Link to="/bodyguard">
                <BodyGuardIcon className="menuIcon"/>
                <span>Bodyguard</span>
            </Link>
            <Link to="/team">
                <TeamIcon className="menuIcon"/>
                <span>Team</span>
            </Link>
            <Link to="/setting">
                <SettingsIcon className="menuIcon"/>
                <span>Settings</span>
            </Link>
            <Link to="/notification">
                <NotificationIcon className="menuIcon"/>
                <span>Notification</span>
            </Link>
            
        </Menu>
        <Logout>
          <LogoutIcon/>
          <span>Logout</span>
        </Logout>

    </SidebarContainer>
  )
}

export default Sidebar;

const SidebarContainer = Styled.div`
    display:flex;
    flex-direction: column;
    justify-content: space-between;
    position:sticky;
    background:white;
    width:200px;
    top: 50px;
    height: calc(100vh - 50px);
    left:0;
    bottom:0;
    
    box-shadow: 0 0 10px rgba(0,0,0,.2);
    opacity:${props=>props.show? 'opacity(1)':'opacity(0)'};
    transform:${props=>props.show? 'scaleX(1)':'scaleX(0)'};
    .menuIcon{
      font-weight:bold;
      font-size:20px;
      margin-right: 6px;

    }



`
const CloseContainer = Styled.div`

`
const CloseBtn = Styled(AiOutlineClose)`
transform:translateX(40px);

`
const Menu = Styled.div`
display:flex;
justify-content:space-between;
align-items:start;
flex-direction:column;
margin-bottom: 2rem;
padding: 5px;
margin: 10px;
z-index:99;

a{
  padding:10px;
  display:flex;
  align-items:center;
  justify-content:center;
  cursor:pointer;
 text-decoration: none !important;

}
&:after{
  span:hover {
    background-color: red;
  }
}


`
const Logout = Styled.div`

bottom:0px !important;
font-weight:400;
margin-bottom:2px;
position:absolute;
margin:10px;
display:flex;
align-items: center;
justify-content:space-between;




`
//defining icons
const LogoutIcon = Styled(AiOutlineLogout)`
`

const DashboardIcon= Styled(AiOutlineHome)`
`

const ClientIcon = Styled(AiOutlineUser)`
`

const BodyGuardIcon= Styled(GiGuards)`
`
const TeamIcon = Styled(AiOutlineTeam)`
`

const SettingsIcon= Styled(AiOutlineSetting)`
`
const NotificationIcon = Styled(AiOutlineNotification)`
`
