import React from 'react'

import Styled from 'styled-components';
import {IoIosNotificationsOutline} from 'react-icons/io'
const Header=()=> {
  return (
    <HeaderContainer>
   
       <Logo>
           WD
       </Logo>
       {// Right Primary menu will contain the profile info, notifications, message and 
       //any other important informations as requirements
       }
       <RightPrimaryMenu>
         {
         //notification button 
         }
            <Notification>
                <NotificationIcon/>
            </Notification>
            {
           //Profile button 
          }
            <Profile>
            <img src="https://images.pexels.com/photos/1526814/pexels-photo-1526814.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500" alt="" className="topAvatar" />
            </Profile>
       </RightPrimaryMenu>
    </HeaderContainer>
  )
}

export default Header

const HeaderContainer = Styled.div`
background:white;
display:flex;
justify-content:space-between;
align-items:center;
top:0;
right:0;
bottom:0;
margin:0;
padding:0;
position:relative;
width:100%;
height:100px;
padding: 1px 10px;
border-bottom:1px solid black;
box-shadow:0 0 10px black;
`
const Logo = Styled.div`
  color:red;
  font-weight:600;
  font-size:24px;
  border: 1px solid red;
  padding: 2px;
`
const RightPrimaryMenu = Styled.div`
display: flex;
align-items: center;

`
const Notification = Styled.div`
width: 15px;
height: 15px;
position: absolute;
top: -5px;
right: 0px;
background-color: red;
color: white;
border-radius: 50%;
display: flex;
align-items: center;
justify-content: center;
font-size: 10px;

`
const Profile = Styled.div`

img{
  width: 40px;
height: 40px;
border-radius: 50%;
cursor: pointer;
}

`
const NotificationIcon = Styled(IoIosNotificationsOutline)`

`
