import React,{useState} from 'react'
import Modal from 'react-modal'
import Styled from 'styled-components'

import {Link } from 'react-router-dom'
import Form from './Form'
function AddBodyguard() {
    const [modalOpen, setModalOpen] = useState(false);
    const [addUser, setAddUser] = useState(false);

   
  return (
    <Container>
        <button type="button" onClick={()=>setModalOpen(true)}>Open Modal</button>
      
             <FormModal isOpen={modalOpen}>
             
               <h4>Add New Bodyguard</h4>
               <Form/>

               <button type="button" onClick={()=>setModalOpen(false)}>Close Modal</button>
           </FormModal> 
    </Container>
  )
}

export default AddBodyguard
const Backdrop = Styled.div`
position:fixed;
backgroud:black;
height:100vh;
width:100vw;
`
const Container = Styled.div`
width:100%;
height:100vh;
background:#ccc;
z-index: 9999 !important;
`


const FormModal = Styled(Modal)`

width: 60%;
height:90vh;
padding:10px;
z-index: 9999 !important;
background:#FFFFFF;


right: 0;
transform:translate(25%, 10%);
box-shadow: 0 0 5px rgba(0,0,0,.5);
button{
    right: 0;
    bottom: 0;
}

`
